import React from 'react';

const RestaurantCard = ({ restaurant }) => {
  return (
    <div className="max-w-sm rounded overflow-hidden shadow-lg bg-white">
      {restaurant.image && (
        <img className="w-full h-48 object-cover" src={restaurant.image} alt={restaurant.name} />
      )}
      <div className="px-6 py-4">
        <div className="font-bold text-xl mb-2">{restaurant.name}</div>
        <p className="text-gray-700 text-base mb-2">
          {restaurant.cuisine} • {restaurant.diningStyle} • {restaurant.budget}
        </p>
        <p className="text-gray-600 text-sm">Distance: {restaurant.distance} miles</p>
        <p className="text-gray-600 text-sm">Rating: {restaurant.rating || 'N/A'}</p>

        {/* Dietary Restrictions Section */}


        {/* Menu Information Section */}
        {restaurant.menu && (
          <div className="mt-3">
            {restaurant.menu.description && (
              <p className="text-gray-600 text-sm mb-2">Description: {restaurant.menu.description}</p>
            )}
            {restaurant.menu.available && restaurant.menu.url && (
              <a 
                href={restaurant.menu.url} 
                target="_blank" 
                rel="noopener noreferrer"
                className="text-blue-500 hover:text-blue-700 text-sm underline"
              >
              </a>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default RestaurantCard;
