import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const LoginPage = ({ setIsLoggedIn }) => {
  const [userId, setUserId] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const res = await axios.post("http://localhost:5000/api/auth/login", {
        userId,
        password,
      });

      // Optional: Store token or user info in localStorage
      localStorage.setItem("userId", JSON.stringify(res.data.user));
      setIsLoggedIn(true);
      alert("Login successful!");
      navigate("/home");
    } catch (err) {
      console.error(err);
      alert("Login failed.");
    }
  };

  return (
    <div style={{width: '100vw', height: '100vh', position: 'relative', background: '#FF5501', overflow: 'hidden'}}>
      <div style={{height: 2268, background: '#FFF6EC', borderRadius: 50, justifyContent: 'flex-start', alignItems: 'center', gap: 10, display: 'inline-flex'}}>

</div>
</div>
  );
};

export default LoginPage;
