import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const LoginPage = ({ setIsLoggedIn }) => {
  const [userId, setUserId] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const res = await axios.post("http://localhost:5000/api/auth/login", {
        userId,
        password,
      });

      // Optional: Store token or user info in localStorage
      localStorage.setItem("userId", JSON.stringify(res.data.user));
      setIsLoggedIn(true);
      alert("Login successful!");
      navigate("/home");
    } catch (err) {
      console.error(err);
      alert("Login failed.");
    }
  };

  return (
    <div style={{width: '100vw', height: '100vh', position: 'relative', background: '#FF5501', overflow: 'hidden'}}>
      <div style={{height: 2268, background: '#FFF6EC', borderRadius: 50, justifyContent: 'flex-start', alignItems: 'center', gap: 10, display: 'inline-flex'}}>
  <div style={{width: 1383, height: 313.94, textAlign: 'center', justifyContent: 'center', display: 'flex', flexDirection: 'column', color: '#FF5501', fontSize: 150, fontFamily: 'Noto Sans JP', fontWeight: '900', wordWrap: 'break-word'}}>Log in</div>
  <div style={{width: 1260.69, height: 157.92, background: 'white', border: '1px black solid'}} />
  <div style={{width: 1260.69, height: 68.50, justifyContent: 'center', display: 'flex', flexDirection: 'column', color: 'black', fontSize: 65, fontFamily: 'Noto Sans JP', fontWeight: '900', wordWrap: 'break-word'}}>Username</div>
  <div style={{width: 1260.69, height: 157.92, background: 'white', border: '1px black solid'}} />
  <div style={{width: 1268.09, height: 68.50, justifyContent: 'center', display: 'flex', flexDirection: 'column', color: 'black', fontSize: 65, fontFamily: 'Noto Sans JP', fontWeight: '900', wordWrap: 'break-word'}}>Password</div>
  <div style={{width: 677.39, height: 144.60, paddingLeft: 315, paddingRight: 315, paddingTop: 16, paddingBottom: 16, background: 'radial-gradient(ellipse 50.00% 50.00% at 50.00% 50.00%, #FF7F36 19%, #FF5501 100%)', borderRadius: 60, justifyContent: 'center', alignItems: 'center', gap: 10, display: 'flex'}}>
    <div style={{textAlign: 'center', justifyContent: 'center', display: 'flex', flexDirection: 'column', color: 'white', fontSize: 100, fontFamily: 'Noto Sans JP', fontWeight: '900', wordWrap: 'break-word'}}>Log in</div>
  </div>
  <div style={{width: 1019.26, height: 186.38, textAlign: 'center', justifyContent: 'center', display: 'flex', flexDirection: 'column', color: '#FF5501', fontSize: 60, fontFamily: 'Noto Sans JP', fontWeight: '900', wordWrap: 'break-word'}}>Donâ€™t Have an Account? Sign up</div>
</div>
</div>
  );
};

export default LoginPage;
