import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./signup.css"; // Import CSS
import burgerImage from '..//landing/munch.png';

const SignUpPage = () => {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    name: "",
    userId: "",
    password: "",
    confirmPassword: "",
    dietaryRestrictions: [],
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;

    if (type === "checkbox") {
      setForm((prev) => {
        const newRestrictions = checked
          ? [...prev.dietaryRestrictions, value]
          : prev.dietaryRestrictions.filter((d) => d !== value);
        return { ...prev, dietaryRestrictions: newRestrictions };
      });
    } else {
      setForm((prev) => ({ ...prev, [name]: value }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (form.password !== form.confirmPassword) {
      alert("Passwords do not match!");
      return;
    }

    try {
      await axios.post("http://localhost:5000/api/auth/signup", {
        name: form.name,
        userId: form.userId,
        password: form.password,
        dietaryRestrictions: form.dietaryRestrictions,
      });
      alert("Signup successful!");
      navigate("/login");
    } catch (err) {
      console.error(err);
      alert("Signup failed.");
    }
  };

  return (
    <div>
    <div className="signup-container">
      <div className="signup-box">
        <form className="signup-form" onSubmit={handleSubmit}>
          <h2>Sign Up</h2>
          <input
            name="name"
            placeholder="Name"
            type="text"
            onChange={handleChange}
            required
          />
          <input
            name="userId"
            placeholder="Username"
            type="text"
            onChange={handleChange}
            required
          />
          <input
            name="password"
            type="password"
            placeholder="Password"
            onChange={handleChange}
            required
          />
          <input
            name="confirmPassword"
            type="password"
            placeholder="Confirm Password"
            onChange={handleChange}
            required
          />

          <div className="checkbox-group">
            <label>
              <input type="checkbox" value="vegetarian" onChange={handleChange} />
              Vegetarian
            </label>
            <label>
              <input type="checkbox" value="vegan" onChange={handleChange} />
              Vegan
            </label>
            <label>
              <input type="checkbox" value="gluten-free" onChange={handleChange} />
              Gluten-Free
            </label>
          </div>

          <button type="submit">Sign up</button>
        </form>

        <div className="signup-image">
          <img src={burgerImage} alt="Sign up graphic" />
        </div>
      </div>
    </div>
    </div>
  );
};

export default SignUpPage;
